{
  "date": 1425916390, // Unix epoch time of the video record
  "rich-media": true, // true if package contains presentation images
  "filename": "video.mp4", // The name of the video file in the package
  "duration": 30, // Duration of the video in seconds
  "indexes": [ // The list of indexes in the video
    {
      "type": "image", // Index type (could be "image" or "tag")
      "timecode": 0, // Index time (in ms) from the beginning of the video
      "data": { // Index data (only for "image" type)
        "filename": "slide_00000.jpeg" // The name of the image file in the tar
      }
    },
    {
      "type": "tag", // Index type (could be "image" or "tag")
      "timecode": 3208 // Index time (in ms) from the beginning of the video
    },
    ...
  ]
}

// Create a new watcher
var watcher = new Watcher();

// Listen to watcher events
watcher.on('create', function(resourcePath) {
  console.log('A new file has been added : ' + resourcePath);
});

watcher.on('delete', function(resourcePath) {
  console.log('A file has been removed : ' + resourcePath);
});

watcher.on('watch', function(directoryPath) {
  console.log('A watcher is now running for directory ' + directoryPath);
});

watcher.on('error', function(error) {
  console.log(error);
});

// Ask watcher to watch directories
watcher.add([
  '/tmp/hotFolder1',
  '/tmp/hotFolder2',
  '/tmp/hotFolder3'
], function(results) {
  console.log(results);
});

// Ask watcher to stop watching directories
watcher.remove([
  '/tmp/hotFolder3'
]);

var coreApi = process.api.getCoreApi();
var database = coreApi.getDatabase();
var PublishManager = process.requirePublish('app/server/PublishManager.js');
var videoProvider = new VideoProvider(database);
var publishManager = new PublishManager(videoProvider, 5);

// Listen publish manager's errors
publishManager.on('error', function(error) {
  // Do something
});

// Listen to publish manager's end of processing for a media
publishManager.on('complete', function(mediaPackage){
  // Do something
});

// Listen to publish manager's event informing that a media processing is retrying
publishManager.on('retry', function(mediaPackage) {
  // Do something
});

// Listen to publish manager's event informing that a media, waiting for upload, starts uploading
publishManager.on('upload', function(mediaPackage) {
  // Do something
});

publishManager.publish({
  type: 'youtube', // The media platform to use for this media
  originalPackagePath: '/home/openveo/medias/media-package.tar', // Path of the media package
  originalFileName: 'media-package' // File name without extension
});

var publishApi = process.api.getApi('publish');
var PUBLISH_HOOKS = publishApi.getHooks();
publishApi.registerAction(PUBLISH_HOOKS.PROPERTIES_DELETED, function(ids, callback) {
  console.log(ids);
  callback();
);

